import React from 'react'


const Header = (props) => {

  return (
    <h1>{props.course.name}</h1>
  )
}


const Part = (props) => {

  return (
    <p>{props.name}, {props.numberOfExercise}</p>
  )
}


const Content = (props) => {

  const info0 = props.course.parts[0];
  const info1 = props.course.parts[1];
  const info2 = props.course.parts[2];

  return(
  <div>
      <Part name = {info0.name} numberOfExercise = {info0.exercises}/>
      <Part name = {info1.name} numberOfExercise = {info1.exercises}/>
      <Part name = {info2.name} numberOfExercise = {info2.exercises}/>
    </div>
  )
}

const Total = (props) => {

  return (
    <p>Number of exercises: {props.course.parts[0]} + {props.course.parts[1]} + {props.course.parts[2]} </p>
  )
}



const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
    <Header course={course} />
    <Content course={course} />
    <Total course={course} />
  </div>
  )
}

export default App
