import React from 'react'


const Header = (props) => {

  return (
    <h1>{props.name}</h1>
  )
}

const Part = (props) => {

  return (
    <p>{props.name} , {props.numberOfExercise}</p>
  )
}


const Content = (props) => {

  <div>
      <Part name = {this.props.name} numberOfExercise = {this.props.numberOfExercise}/>
      <Part name = {this.props.name} numberOfExercise = {this.props.numberOfExercise}/>
      <Part name = {this.props.name} numberOfExercise = {this.props.numberOfExercise}/>
    </div>
}

const Total = (props) => {

  return (
    <>
    {props.name}
    </>
  )
}



const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header name = {course}/>
      <Content  name = {part1} numberOfExercise={exercises1}/>
      <Content name = {part2} numberOfExercise= {exercises2}/>
      <Content name = {part3} numberOfExercise=  {exercises3}/>        


      <p>Number of exercises: <Total name = {exercises1 + exercises2 + exercises3}/></p>
    </div>
  )
}

export default App
